    private void btnhitungjumlahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnhitungjumlahActionPerformed
        int JumlahBarang=Integer.parseInt(txtjumlahbeli.getText());
        int HargaBarang=Integer.parseInt(txtharga.getText());
        txtjumlahharga.setText(Integer.toString(HargaBarang*JumlahBarang));
    }//GEN-LAST:event_btnhitungjumlahActionPerformed

    private void btnbatalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnbatalActionPerformed
        txtharga.setText("");
       txtjumlahbeli.setText("");
       txtjumlahharga.setText("");
       txtjumlahbayar.setText("");
       txtjumlahkembalian.setText("");
       nama_menu.setSelectedIndex(0);
    }//GEN-LAST:event_btnbatalActionPerformed

