    private void btnbayarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnbayarActionPerformed
         int JumlahBayar=Integer.parseInt(txtjumlahbayar.getText());
       int JumlahHarga=Integer.parseInt(txtjumlahharga.getText());
       if(JumlahBayar>=JumlahHarga){
          txtjumlahkembalian.setText(Integer.toString(JumlahBayar-JumlahHarga));
       }
       else{
           txtjumlahkembalian.setText("Uang Anda Kurang");
       }
    }//GEN-LAST:event_btnbayarActionPerformed

